// Need for iOS mobile device to get motion events
function permissionAction() {
  if (typeof DeviceMotionEvent !== 'undefined' && typeof DeviceMotionEvent.requestPermission === 'function') {
    // (optional) Do something before API request prompt.
    DeviceMotionEvent.requestPermission()
      .then((response) => {
        console.log('requestPermission response', response);
        // (optional) Do something after API prompt dismissed.
        if (response == 'granted') {
          window.addEventListener('devicemotion', (e) => {
            // console.log('devicemotion e', e)
            // console.log('devicemotion e.beta', e.beta)
          });
        }
      })
      .catch(console.error);
  } else {
    alert('DeviceMotionEvent is not defined');
  }
}

//
function draw_number(n, sz) {
  // Convert number to string
  let str = (n + '').padStart(2, '0');
  let x = 10;
  let y = my.height;
  textSize(sz);
  // Draw black rect background
  let a = textAscent();
  let d = textDescent();
  let h = a + d;
  let w = textWidth(str);
  fill(0);
  // rect(x, y - h, w, h);
  rect(x, 0, w, my.height);

  // Draw white text
  fill(255);
  // x  y bottom-left corner.
  text(str, x, y - (my.height - h));
}

