  createCanvas(windowWidth, windowHeight - 55);
  // I made the canvas really small because it's slow for me otherwise
  // createCanvas(200, 200);
  pixelDensity(1);
  initGraphics();
  noise = new OpenSimplexNoise(Date.now());
  setup_fullScreenBtn();
}

function draw() {
  updateLayer();
  let w = layer.width;
  let h = layer.height;
  image(layer, 0, 0, width, height, 0, 0, w, h);
}

function initGraphics() {
  layer = createGraphics(100, 100);
}

function updateLayer() {
  let xoff = 0;
  let w = layer.width;
  let h = layer.height;
  for (let x = 0; x < w; x++) {
    let yoff = 0;
    for (let y = 0; y < h; y++) {
      let n;
      n = noise.noise3D(xoff, yoff, zoff);
      // console.log('n',n)
      // let bright = n > 0 ? 255 : 0;
      let bright = map(n, -1, 1, 0, 255);
      layer.stroke(bright);
      layer.point(x, y);
      yoff += increment;
    }
    xoff += increment;
  }
  zoff += incrementZ;
}

