function nstep_init() {
  if (my.nstep < 4) {
    my.margin = 0;
  } else {
    my.margin = 0.1;
  }
  // my.stepPx = floor(my.vwidth / my.nstep);
  my.stepPx = floor(my.vheight / my.nstep);
  my.innerPx = floor(my.stepPx * (1 - my.margin));
  my.crossWt = my.stepPx - my.innerPx;
  my.byPixel = my.nstep < 16;
  my.byLine = my.nstep >= 16;
}

