// video radial perlin pix

let npix = 64;
let nwidth = 480;
let nheight = 640;
let xgap = nwidth / 4;
let ygap = nheight / 4;
let x0;
let y0;
let capture;
let ang = 0;
let astep = 0.3;
let astart = 0; //180;
let aend = 360;
let rscale = 2;
let routterScale = 1;

let phase = 0;
let zoff = 0;
let noiseMax = 5;

let img2;

function setup() {
  createCanvas(nwidth, nheight);
  capture = createCapture(VIDEO);
  capture.size(width, height);
  capture.hide();
  x0 = int(nwidth / 2);
  y0 = int(nheight / 2);
}

function draw() {
  // colorMode(HSB);
  strokeWeight(1);
  // background(0);

  // image(img, dx, dy, dWidth, dHeight, sx, sy, [sWidth], [sHeight],
  let img = capture.get();
  img2 = img.get();
  img2.resize(npix, 0);
  image(img2, 0, 0, nwidth, nheight);

  draw_rad(img);
  // draw_noise()
}

function draw_rad(img) {
  for (let ang = astart; ang < aend; ang += astep) {
    let r1 = xgap / 2;
    let rang = radians(ang);
    // let x1 = r1 * cos(rang);
    // let y1 = r1 * sin(rang);

    let xoff = map(cos(rang + phase), -1, 1, 0, noiseMax);
    let yoff = map(sin(rang + phase), -1, 1, 0, noiseMax);
    let r2 = map(noise(xoff, yoff, zoff), 0, 1, r1, r1 * rscale);
    let x1 = r2 * cos(rang);
    let y1 = r2 * sin(rang);

    let c1 = img.get(x0 + x1, y0 + y1);
    stroke(c1);
    fill(x1);
    fill(0);
    circle(x0 + x1, y0 + y1, 5);

    let r3 = nwidth * routterScale + random(-5, 5);
    let x2 = r3 * cos(rang);
    let y2 = r3 * sin(rang);
    line(x0 + x1, y0 + y1, x0 + x2, y0 + y2);
  }
  phase += 0.003;
  zoff += 0.01;
}

function draw_noise() {
  // background(0);
  translate(width / 2, height / 2);
  stroke(255);
  strokeWeight(2);
  noFill();
  beginShape();
  // let noiseMax = slider.value();
  for (let a = 0; a < TWO_PI; a += radians(5)) {
    let xoff = map(cos(a + phase), -1, 1, 0, noiseMax);
    let yoff = map(sin(a + phase), -1, 1, 0, noiseMax);
    let r = map(noise(xoff, yoff, zoff), 0, 1, 100, height / 2);
    let x = r * cos(a);
    let y = r * sin(a);
    vertex(x, y);
  }
  endShape(CLOSE);
  phase += 0.003;
  zoff += 0.01;
}

